import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Toast from '../components/Toast';

const TokenCreation = () => {
  const [name, setName] = useState('');
  const [tokens, setTokens] = useState([]);
  const [description, setDescription] = useState('');
  const [supply, setSupply] = useState('');
  const [type, setType] = useState('Releasable');
  const [decimals, setDecimals] = useState(2);
  const [loading, setLoading] = useState(false);
  const [toast, setToast] = useState('');
  const navigate = useNavigate();

  const token = localStorage.getItem('token');
  const headers = {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${token}`
  };

  useEffect(() => {
    axios.get('https://profitpilot-backend.onrender.com/api/tokens', { headers })
      .then(res => setTokens(res.data || []))
      .catch(err => console.error(err));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name.trim()) return alert('Token name is required.');
    if (!supply || isNaN(supply) || parseFloat(supply) <= 0) {
      return alert('Please enter a valid token supply.');
    }

    if (tokens.length >= 5) {
      return alert('Maximum of 5 tokens allowed.');
    }

    if (tokens.length >= 1) {
      const confirmPay = confirm('Creating this token costs $5. Do you want to proceed?');
      if (!confirmPay) return;
    }

    setLoading(true);

    try {
      const res = await axios.post(
        'https://profitpilot-backend.onrender.com/api/tokens',
        { name, description, supply, type, decimals },
        { headers }
      );
      setToast('Token created successfully!');
      setTokens(res.data.tokens);
      setName('');
      setDescription('');
      setSupply('');
      setType('Releasable');
      setDecimals(2);

      if (res.data.tokens.length === 1) {
        setTimeout(() => navigate('/dashboard'), 1000);
      }
    } catch (err) {
      console.error('Token creation error:', err.response?.data || err);
      alert(err.response?.data?.msg || err.message || 'Something went wrong.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white px-4 py-8">
      {toast && <Toast message={toast} onClose={() => setToast('')} />}
      <div className="max-w-xl mx-auto bg-[#121212] rounded-2xl shadow-lg p-6">
        <h2 className="text-2xl font-semibold text-yellow-400 mb-1 text-center">Create Your Token</h2>
        <p className="text-sm text-gray-400 mb-4 text-center">{5 - tokens.length} tokens left</p>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="text-sm mb-1 block">Token name</label>
            <input
              type="text"
              className="w-full px-3 py-2 bg-zinc-900 border border-gray-700 rounded-md"
              placeholder="SomeTokenName"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          <div>
            <label className="text-sm mb-1 block">Description</label>
            <textarea
              className="w-full px-3 py-2 bg-zinc-900 border border-gray-700 rounded-md"
              placeholder="Token description"
              maxLength={160}
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </div>

          <div>
            <label className="text-sm mb-1 block">Amount of Issued Tokens</label>
            <input
              type="number"
              className="w-full px-3 py-2 bg-zinc-900 border border-gray-700 rounded-md"
              placeholder="1000"
              value={supply}
              onChange={(e) => setSupply(e.target.value)}
            />
          </div>

          <div>
            <label className="text-sm mb-1 block">Token Type</label>
            <select
              className="w-full px-3 py-2 bg-zinc-900 border border-gray-700 rounded-md"
              value={type}
              onChange={(e) => setType(e.target.value)}
            >
              <option value="Releasable">Releasable</option>
              <option value="Fixed">Fixed</option>
            </select>
          </div>

          <div>
            <label className="text-sm mb-2 block">Decimals: {decimals}</label>
            <input
              type="range"
              min="0"
              max="18"
              value={decimals}
              onChange={(e) => setDecimals(e.target.value)}
              className="w-full"
            />
          </div>

          <button
            type="submit"
            disabled={loading || tokens.length >= 5}
            className={`w-full py-2 rounded-md font-semibold transition-all ${
              loading || tokens.length >= 5
                ? 'bg-gray-600 cursor-not-allowed'
                : 'bg-yellow-500 hover:bg-yellow-600'
            }`}
          >
            {loading ? 'Creating...' : tokens.length === 0 ? 'Create Free Token' : 'Create Token ($5)'}
          </button>
        </form>
      </div>

      {tokens.length > 0 && (
        <div className="mt-8 max-w-xl mx-auto">
          <h3 className="text-lg font-semibold text-yellow-400 mb-3">Your Tokens</h3>
          <ul className="space-y-2">
            {tokens.map((token, index) => (
              <li key={index} className="bg-zinc-800 px-4 py-2 rounded-md border border-zinc-700">
                <p className="font-medium">{token.name}</p>
                <p className="text-sm text-gray-400">
                  Created on: {new Date(token.performanceData[0].date).toLocaleString()}
                </p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default TokenCreation;
