import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Toast from '../components/Toast';

const TokenCreation = () => {
  const [name, setName] = useState('');
  const [tokens, setTokens] = useState([]);
  const [loading, setLoading] = useState(false);
  const [toast, setToast] = useState('');
  const navigate = useNavigate();

  const token = localStorage.getItem('token');
  const headers = {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${token}`
  };

  useEffect(() => {
    axios.get('https://profitpilot-backend.onrender.com/api/tokens', { headers })
      .then(res => setTokens(res.data.tokens || []))
      .catch(err => console.error(err));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name.trim()) return alert('Token name is required.');
    setLoading(true);

    try {
      const res = await axios.post(
        'https://profitpilot-backend.onrender.com/api/tokens',
        { name },
        { headers }
      );
      setToast('Token created successfully!');
      setTokens(res.data.tokens);
      setName('');
      if (res.data.tokens.length === 1) {
        setTimeout(() => navigate('/dashboard'), 1200);
      }
    } catch (err) {
      alert(err.response?.data?.msg || 'Something went wrong.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white px-4 py-8 flex flex-col items-center justify-center">
      {toast && <Toast message={toast} onClose={() => setToast('')} />}
      <div className="w-full max-w-md bg-[#121212] rounded-2xl shadow-lg p-6">
        <h2 className="text-2xl font-semibold text-yellow-400 mb-2 text-center">Create Your Token</h2>
        <p className="text-sm text-gray-300 mb-4 text-center">You have created {tokens.length}/5 tokens</p>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            className="w-full px-4 py-2 bg-zinc-900 border border-gray-700 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-400"
            placeholder="Enter token name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <button
            type="submit"
            disabled={loading || tokens.length >= 5}
            className={`w-full py-2 rounded-md transition-all font-semibold ${
              loading || tokens.length >= 5
                ? 'bg-gray-600 cursor-not-allowed'
                : 'bg-yellow-500 hover:bg-yellow-600'
            }`}
          >
            {loading ? 'Creating...' : tokens.length === 0 ? 'Create Free Token' : 'Create Token ($5)'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default TokenCreation;
