import React, { useEffect, useState } from "react";
import axios from "axios";

const Referral = () => {
  const [referrals, setReferrals] = useState(0);
  const [link, setLink] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    axios
      .get("https://profitpilot-backend.onrender.com/api/users/profile", {
        headers: { "x-auth-token": token },
      })
      .then((res) => {
        setReferrals(res.data.referrals);
        setLink(`https://profit-pilot-frontend.vercel.app/register?ref=${res.data._id}`);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

  return (
    <div className="p-4 text-white">
      <h2 className="text-xl font-bold mb-4">Referral Program</h2>
      <p className="mb-2">Youâ€™ve referred: <strong>{referrals}</strong> users</p>
      <p className="mb-2">You earn <strong>$2</strong> per successful referral.</p>
      <div className="bg-gray-800 p-3 rounded-lg break-all">
        <strong>Your Referral Link:</strong>
        <p className="text-sm text-green-400">{link}</p>
      </div>
    </div>
  );
};

export default Referral;
