import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const TokenCreation = () => {
  const [name, setName] = useState('');
  const [tokens, setTokens] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const token = localStorage.getItem('token');
  const headers = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  useEffect(() => {
    axios.get('https://profitpilot-backend.onrender.com/api/users/profile', { headers })
      .then(res => {
        setTokens(res.data.tokens || []);
      })
      .catch(err => {
        console.error(err);
      });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name) return alert('Token name required');
    setLoading(true);

    try {
      const res = await axios.post(
        'https://profitpilot-backend.onrender.com/api/users/create-token',
        { name },
        { headers }
      );
      alert(res.data.msg);
      setTokens(res.data.tokens);
      setName('');
      if (res.data.tokens.length === 1) {
        navigate('/dashboard');
      }
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.msg || 'Token creation failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="token-creation-page">
      <h2>Create Your Token</h2>
      <p>You have created {tokens.length}/5 tokens</p>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter token name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>
          {tokens.length === 0 ? 'Create Free Token' : 'Create Token ($5)'}
        </button>
      </form>
    </div>
  );
};

export default TokenCreation;
