import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaBars, FaTimes, FaUserCircle } from 'react-icons/fa';

const Layout = ({ children }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => setIsOpen(!isOpen);

  return (
    <div className="flex h-screen bg-black text-white">
      {/* Sidebar */}
      <div
        className={`fixed top-0 left-0 z-40 h-full w-64 bg-[#1a1a1a] shadow-md transform ${
          isOpen ? 'translate-x-0' : '-translate-x-full'
        } transition-transform duration-300 ease-in-out`}
      >
        <div className="p-6 text-orange-500 font-bold text-xl border-b border-gray-700">Menu</div>
        <ul className="p-4 space-y-4">
          <li><Link to="/dashboard" className="text-white hover:text-orange-500">Dashboard</Link></li>
          <li><Link to="/tokens" className="text-white hover:text-orange-500">My Tokens</Link></li>
          <li><Link to="/boost" className="text-white hover:text-orange-500">Boost</Link></li>
          <li><Link to="/referrals" className="text-white hover:text-orange-500">Referrals</Link></li>
          <li><Link to="/withdraw" className="text-white hover:text-orange-500">Withdraw</Link></li>
        </ul>
      </div>

      {/* Main content */}
      <div className="flex-1 flex flex-col">
        {/* Topbar */}
        <div className="flex items-center justify-between p-4 bg-[#111] shadow-md">
          <button onClick={toggleSidebar} className="text-orange-500 text-xl">
            {isOpen ? <FaTimes /> : <FaBars />}
          </button>
          <h1 className="text-xl font-bold text-orange-500">ProfitPilot</h1>
          <FaUserCircle className="text-2xl text-orange-500" />
        </div>

        {/* Page Content */}
        <main className="p-4 overflow-y-auto">{children}</main>
      </div>
    </div>
  );
};

export default Layout;
