import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, Link, useLocation } from 'react-router-dom';
import '../styles.css';

const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  const getReferralId = () => {
    const params = new URLSearchParams(location.search);
    return params.get('ref') || null;
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const referredBy = getReferralId();
    try {
      const res = await axios.post('https://profitpilot-backend.onrender.com/api/register', {
        email,
        password,
        referredBy
      });
      localStorage.setItem('token', res.data.token);
      navigate('/welcome');
    } catch (err) {
      setError('Registration failed. Try another email.');
    }
  };

  return (
    <div className="auth-container">
      <h2>Register on ProfitPilot</h2>
      {error && <p className="error-text">{error}</p>}
      <form onSubmit={handleRegister}>
        <input
          type="email"
          placeholder="Email address"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        <button type="submit">Register</button>
      </form>
      <p className="alt-link">Already have an account? <Link to="/login">Login</Link></p>
    </div>
  );
};

export default Register;
