import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles.css';

const Leaderboard = () => {
  const [leaders, setLeaders] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const res = await axios.get(
          'https://profitpilot-backend.onrender.com/api/leaderboard',
          {
            headers: {
              'x-auth-token': token,
            },
          }
        );
        setLeaders(res.data.leaders);
        setCurrentUser(res.data.currentUser);
      } catch (err) {
        console.error('Failed to fetch leaderboard:', err.message);
      }
    };
    fetchLeaderboard();
  }, []);

  const mockLeaders = [
    { name: 'BoostMasterX', boostRate: 68 },
    { name: 'CryptoWhale', boostRate: 40 },
    { name: 'PilotKing', boostRate: 31 },
  ];

  return (
    <div className="container leaderboard">
      <h2 className="text-xl font-bold mb-4">Leaderboard</h2>

      <div className="mock-section mb-6">
        <h3 className="text-md font-semibold text-gray-600 mb-2">Top Boosters (Mock)</h3>
        {mockLeaders.map((user, i) => (
          <div key={i} className="mock-item">
            #{i + 1} <span className="font-medium">{user.name}</span> - Boost x{user.boostRate}
          </div>
        ))}
      </div>

      <div className="real-section">
        <h3 className="text-md font-semibold text-gray-600 mb-2">Real Users</h3>
        {leaders.length === 0 ? (
          <p>No boosted users yet.</p>
        ) : (
          leaders.map((user, i) => (
            <div
              key={user._id}
              className={`real-item ${currentUser?._id === user._id ? 'highlight' : ''}`}
            >
              #{i + 1} <span>{user.email}</span> â€” Boost x{user.boostRate}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Leaderboard;
