// src/pages/TokenCreate.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaCoins } from 'react-icons/fa';

const TokenCreate = () => {
  const [tokenName, setTokenName] = useState('');
  const [error, setError] = useState('');
  const [tokens, setTokens] = useState([]);
  const [loading, setLoading] = useState(true);

  const API = 'https://profitpilot-backend.onrender.com/api/tokens';
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchTokens = async () => {
      try {
        const res = await axios.get(API, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTokens(res.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchTokens();
  }, []);

  const createToken = async () => {
    try {
      setError('');
      if (!tokenName.trim()) {
        setError('Token name is required');
        return;
      }

      await axios.post(
        API,
        { name: tokenName }, // âœ… only sending name
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setTokenName('');
      window.location.reload(); // refresh to fetch new list
    } catch (err) {
      console.error(err);
      setError('Error creating token');
    }
  };

  return (
    <div className="text-white">
      <h2 className="text-3xl font-bold text-orange-500 flex items-center gap-2">
        <FaCoins /> My Tokens
      </h2>

      {loading ? (
        <p className="mt-4 text-gray-400">Loading...</p>
      ) : tokens.length === 0 ? (
        <p className="mt-4 text-gray-400">No tokens yet. Create your first token!</p>
      ) : (
        <ul className="mt-4 space-y-2">
          {tokens.map((token, index) => (
            <li key={index} className="text-lg text-gray-300">
              ðŸ”¹ {token.name}
            </li>
          ))}
        </ul>
      )}

      {/* Token Input */}
      <div className="mt-6 flex gap-2">
        <input
          type="text"
          placeholder="Enter token name"
          value={tokenName}
          onChange={(e) => setTokenName(e.target.value)}
          className="p-2 rounded bg-[#111] border border-gray-700 text-white w-full"
        />
        <button
          onClick={createToken}
          className="bg-orange-500 hover:bg-orange-600 text-white p-3 rounded"
        >
          +
        </button>
      </div>

      {error && <p className="text-red-400 mt-2">{error}</p>}

      {/* Fake Non-Functional Inputs for visuals only */}
      <div className="mt-8 space-y-4 text-sm text-gray-400">
        <input
          disabled
          placeholder="Token Description"
          className="w-full p-2 bg-[#111] rounded border border-gray-700"
        />
        <input
          disabled
          placeholder="Issued Tokens (visual)"
          className="w-full p-2 bg-[#111] rounded border border-gray-700"
        />
        <select disabled className="w-full p-2 bg-[#111] rounded border border-gray-700">
          <option>Burnable</option>
          <option>Mintable</option>
        </select>
        <input
          disabled
          type="range"
          min="0"
          max="18"
          className="w-full"
        />
        <button disabled className="bg-gray-700 text-gray-400 w-full p-2 rounded">
          Create Token (visual)
        </button>
      </div>
    </div>
  );
};

export default TokenCreate;
