import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { LogOut, Home, PlusCircle, Zap, Users, BarChart2, User } from 'lucide-react';

const Sidebar = () => {
  const location = useLocation();
  const current = location.pathname;

  const menu = [
    { path: '/dashboard', icon: <Home size={20} />, label: 'Dashboard' },
    { path: '/token-creation', icon: <PlusCircle size={20} />, label: 'Create Token' },
    { path: '/boost', icon: <Zap size={20} />, label: 'Boost' },
    { path: '/referral', icon: <Users size={20} />, label: 'Referrals' },
    { path: '/leaderboard', icon: <BarChart2 size={20} />, label: 'Leaderboard' },
    { path: '/profile', icon: <User size={20} />, label: 'Profile' },
  ];

  const logout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div className="bg-gray-900 text-white w-full h-screen md:w-64 p-6 fixed top-0 left-0 shadow-lg z-10">
      <h1 className="text-2xl font-bold text-purple-500 mb-6">ProfitPilot</h1>
      <nav className="flex flex-col space-y-4">
        {menu.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`flex items-center space-x-2 px-3 py-2 rounded-md transition ${
              current === item.path
                ? 'bg-purple-700 text-white'
                : 'hover:bg-gray-700 text-gray-300'
            }`}
          >
            {item.icon}
            <span>{item.label}</span>
          </Link>
        ))}
        <button
          onClick={logout}
          className="flex items-center space-x-2 px-3 py-2 rounded-md bg-red-600 hover:bg-red-700 text-white transition mt-6"
        >
          <LogOut size={20} />
          <span>Logout</span>
        </button>
      </nav>
    </div>
  );
};

export default Sidebar;
