import React, { useEffect, useState } from 'react';
import axios from 'axios';
import moment from 'moment';

const Dashboard = () => {
  const [userData, setUserData] = useState(null);
  const [miningMsg, setMiningMsg] = useState('');
  const [boostPopup, setBoostPopup] = useState(false);
  const [countdown, setCountdown] = useState('');
  const token = localStorage.getItem('token');

  const headers = {
    'Content-Type': 'application/json',
    'x-auth-token': token
  };

  const fetchUser = async () => {
    try {
      const res = await axios.get('https://profitpilot-backend.onrender.com/api/users/profile', { headers });
      setUserData(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleMine = async () => {
    try {
      const res = await axios.post('https://profitpilot-backend.onrender.com/api/users/mine', {}, { headers });
      setMiningMsg(res.data.msg);
      fetchUser();
    } catch (err) {
      setMiningMsg(err.response?.data?.msg || 'Error mining');
    }
  };

  const handleBoost = async () => {
    try {
      const res = await axios.post('https://profitpilot-backend.onrender.com/api/users/boost', {}, { headers });
      alert(res.data.msg);
      setBoostPopup(false);
      fetchUser();
    } catch (err) {
      alert(err.response?.data?.msg || 'Boost failed');
    }
  };

  const calculateCountdown = () => {
    const start = moment('2025-06-06');
    const end = moment(start).add(6, 'months');
    const now = moment();
    const diff = end.diff(now);

    if (diff <= 0) return 'Withdraw available soon.';
    const duration = moment.duration(diff);
    return `${duration.months()}m ${duration.days()}d ${duration.hours()}h`;
  };

  useEffect(() => {
    fetchUser();
    setInterval(() => {
      setCountdown(calculateCountdown());
    }, 1000 * 60);
  }, []);

  return (
    <div className="dashboard">
      <h2>Dashboard</h2>
      {userData && (
        <div className="summary-box">
          <p><strong>Balance:</strong> ${userData.income.toFixed(2)}</p>
          <p><strong>Boost Rate:</strong> x{userData.boostRate}</p>
          <p><strong>Countdown (No Withdraw):</strong> {countdown}</p>
          <p><strong>Tokens:</strong> {userData.tokens.length}</p>
          <button onClick={handleMine}>Start Mining</button>
          <p>{miningMsg}</p>

          {/* Placeholder Chart */}
          <div className="chart-placeholder">
            <h3>Performance Chart</h3>
            <p>[Chart will appear here]</p>
          </div>

          <button onClick={() => setBoostPopup(true)}>Boost</button>
        </div>
      )}

      {boostPopup && (
        <div className="popup">
          <h3>Simulate $3 Boost?</h3>
          <p>This will increase your mining rate every 2 hours.</p>
          <button onClick={handleBoost}>Confirm Boost</button>
          <button onClick={() => setBoostPopup(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
