import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [tokens, setTokens] = useState([]);
  const [selectedToken, setSelectedToken] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchUserData = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = { headers: { Authorization: `Bearer ${token}` } };

      const userRes = await axios.get('https://profitpilot-backend.onrender.com/api/auth/user', config);
      setUser(userRes.data);

      const tokenRes = await axios.get('https://profitpilot-backend.onrender.com/api/token', config);
      setTokens(tokenRes.data);
      setSelectedToken(tokenRes.data[0] || null);

      setLoading(false);
    } catch (err) {
      console.error('Error loading dashboard:', err);
    }
  };

  useEffect(() => {
    fetchUserData();
  }, []);

  const handleTokenClick = (token) => setSelectedToken(token);

  const handleBoost = async () => {
    // Boost feature logic will be added later
    alert('Boost feature coming soon!');
  };

  if (loading) return <div className="text-center mt-10 text-white">Loading...</div>;

  return (
    <div className="min-h-screen bg-gray-900 text-white p-4">
      {/* Top Bar */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h2 className="text-xl font-semibold">Welcome, {user?.name}</h2>
          <p>Income: ${user?.income?.toFixed(2)}</p>
        </div>
        <div>
          <p className="text-sm">Boost Multiplier: x{user?.boostMultiplier || 1}</p>
        </div>
      </div>

      {/* Chart Section */}
      <div className="bg-gray-800 rounded-lg p-4 mb-6">
        <h3 className="text-lg font-semibold mb-2">{selectedToken?.name || 'No Token Selected'}</h3>
        {selectedToken ? (
          <ResponsiveContainer width="100%" height={200}>
            <LineChart data={selectedToken.performanceData}>
              <XAxis dataKey="date" hide />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="value" stroke="#00cba9" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        ) : (
          <p>No performance data yet</p>
        )}
      </div>

      {/* Boost Button */}
      <div className="flex justify-center mb-6">
        <button
          onClick={handleBoost}
          className="bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded-lg text-lg"
        >
          Boost Performance
        </button>
      </div>

      {/* Token List */}
      <div>
        <h4 className="text-lg font-semibold mb-2">Your Tokens</h4>
        <div className="grid grid-cols-2 gap-3">
          {tokens.map((token) => (
            <div
              key={token.name}
              onClick={() => handleTokenClick(token)}
              className={`p-3 rounded-lg cursor-pointer ${
                selectedToken?.name === token.name ? 'bg-blue-500' : 'bg-gray-800'
              }`}
            >
              {token.name}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
