// src/pages/TokenCreation.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaCoins } from 'react-icons/fa';

const TokenCreation = () => {
  const [tokenName, setTokenName] = useState('');
  const [error, setError] = useState('');
  const [tokens, setTokens] = useState([]);
  const [loading, setLoading] = useState(true);

  const API = 'https://profitpilot-backend.onrender.com/api/token';
  const authToken = localStorage.getItem('token');

  // Fetch tokens on mount
  useEffect(() => {
    const fetchTokens = async () => {
      try {
        const res = await axios.get(API, {
          headers: { Authorization: `Bearer ${authToken}` },
        });
        setTokens(res.data);
      } catch (err) {
        console.error('Fetch error:', err);
        setError('Failed to load tokens');
      } finally {
        setLoading(false);
      }
    };

    fetchTokens();
  }, []);

  const handleCreateToken = async () => {
    setError('');
    if (!tokenName.trim()) {
      return setError('Token name is required');
    }

    try {
      const res = await axios.post(
        API,
        { name: tokenName },
        {
          headers: { Authorization: `Bearer ${authToken}` },
        }
      );

      setTokens(res.data.tokens); // update list without reload
      setTokenName('');
    } catch (err) {
      console.error('Create error:', err);
      const msg = err?.response?.data?.msg || 'Error creating token';
      setError(msg);
    }
  };

  return (
    <div className="text-white p-4">
      <h2 className="text-3xl font-bold text-orange-500 flex items-center gap-2">
        <FaCoins /> My Tokens
      </h2>

      {loading ? (
        <p className="mt-4 text-gray-400">Loading...</p>
      ) : tokens.length === 0 ? (
        <p className="mt-4 text-gray-400">No tokens yet. Create your first token!</p>
      ) : (
        <ul className="mt-4 space-y-2">
          {tokens.map((token, index) => (
            <li key={index} className="text-lg text-gray-300">
              ðŸ”¹ {token.name}
            </li>
          ))}
        </ul>
      )}

      <div className="mt-6 flex gap-2">
        <input
          type="text"
          placeholder="Enter token name"
          value={tokenName}
          onChange={(e) => setTokenName(e.target.value)}
          className="p-2 rounded bg-[#111] border border-gray-700 text-white w-full"
        />
        <button
          onClick={handleCreateToken}
          className="bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded"
        >
          +
        </button>
      </div>

      {error && <p className="text-red-400 mt-2">{error}</p>}

      {/* Optional: visuals only - disabled UI */}
      <div className="mt-8 space-y-4 text-sm text-gray-400">
        <input disabled placeholder="Token Description" className="w-full p-2 bg-[#111] rounded border border-gray-700" />
        <input disabled placeholder="Issued Tokens (visual)" className="w-full p-2 bg-[#111] rounded border border-gray-700" />
        <select disabled className="w-full p-2 bg-[#111] rounded border border-gray-700">
          <option>Burnable</option>
          <option>Mintable</option>
        </select>
        <input disabled type="range" min="0" max="18" className="w-full" />
        <button disabled className="bg-gray-700 text-gray-400 w-full p-2 rounded">
          Create Token (visual)
        </button>
      </div>
    </div>
  );
};

export default TokenCreation;
