import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Layout from '../components/Layout';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  LineElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Tooltip,
} from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip);

const Dashboard = () => {
  const [userData, setUserData] = useState(null);
  const [tokens, setTokens] = useState([]);
  const [income, setIncome] = useState(0);
  const [chartData, setChartData] = useState([]);

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const res = await axios.get('https://profitpilot-backend.onrender.com/api/user/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserData(res.data);
      } catch (err) {
        console.error('User fetch failed:', err);
      }
    };

    const fetchTokens = async () => {
      try {
        const res = await axios.get('https://profitpilot-backend.onrender.com/api/token', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTokens(res.data);
        let total = 0;
        const points = res.data.flatMap((token) =>
          token.performance.map((p) => {
            total += p.value;
            return { time: p.time, value: p.value };
          })
        );
        setChartData(points);
        setIncome(total.toFixed(2));
      } catch (err) {
        console.error('Token fetch failed:', err);
      }
    };

    fetchUserData();
    fetchTokens();
  }, [token]);

  const chartOptions = {
    responsive: true,
    plugins: {
      tooltip: { mode: 'index', intersect: false },
    },
    scales: {
      x: { ticks: { color: '#aaa' } },
      y: { ticks: { color: '#aaa' } },
    },
  };

  const lineData = {
    labels: chartData.map((p) => new Date(p.time).toLocaleTimeString()),
    datasets: [
      {
        label: 'Performance',
        data: chartData.map((p) => p.value),
        fill: false,
        borderColor: '#8b5cf6',
        tension: 0.3,
      },
    ],
  };

  return (
    <Layout>
      <div className="space-y-6">
        <h2 className="text-2xl font-bold text-purple-400">Welcome, {userData?.username || 'User'}!</h2>
        <div className="bg-gray-900 p-4 rounded-lg shadow-lg">
          <h3 className="text-xl font-semibold mb-2">Your Total Income</h3>
          <p className="text-3xl font-bold text-green-400">${income}</p>
        </div>

        <div className="bg-gray-900 p-4 rounded-lg shadow-lg">
          <h3 className="text-xl font-semibold mb-2">Token Performance Chart</h3>
          {chartData.length > 0 ? (
            <Line data={lineData} options={chartOptions} />
          ) : (
            <p className="text-gray-400">No performance data yet.</p>
          )}
        </div>

        <div className="bg-gray-900 p-4 rounded-lg shadow-lg text-center">
          <h3 className="text-xl font-semibold mb-2">Boost Your Token</h3>
          <p className="text-gray-400 mb-2">Pay $3 to double your income.</p>
          <a
            href="/boost"
            className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded transition"
          >
            Boost Now
          </a>
        </div>
      </div>
    </Layout>
  );
};

export default Dashboard;
