import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const TokenCreation = () => {
  const [tokens, setTokens] = useState([]);
  const [newTokenName, setNewTokenName] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const API = 'https://profitpilot-backend.onrender.com/api/token';
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchTokens();
  }, []);

  const fetchTokens = async () => {
    try {
      const res = await axios.get(API, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTokens(res.data);
    } catch (err) {
      console.error(err);
      setMessage('Failed to load tokens');
    }
  };

  const handleCreateToken = async () => {
    if (!newTokenName.trim()) {
      return setMessage('Please enter a token name');
    }

    try {
      setLoading(true);
      setMessage('');

      const res = await axios.post(
        API,
        { name: newTokenName },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setTokens(res.data.tokens);
      setNewTokenName('');
      setMessage('Token created successfully!');
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.msg || 'Error creating token');
    } finally {
      setLoading(false);
    }
  };

  const handleDashboard = () => {
    navigate('/dashboard');
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Your Tokens</h1>

      <div className="space-y-2 mb-4">
        {tokens.length === 0 ? (
          <p>No tokens yet. Create your first token!</p>
        ) : (
          tokens.map((t, idx) => (
            <div
              key={idx}
              className="p-3 bg-white shadow rounded flex justify-between items-center"
            >
              <span>{t.name}</span>
            </div>
          ))
        )}
      </div>

      {tokens.length < 5 && (
        <div className="flex gap-2 mb-4">
          <input
            type="text"
            placeholder="New token name"
            className="border rounded p-2 flex-1"
            value={newTokenName}
            onChange={(e) => setNewTokenName(e.target.value)}
          />
          <button
            onClick={handleCreateToken}
            disabled={loading}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            {loading ? 'Creating...' : '+'}
          </button>
        </div>
      )}

      {tokens.length >= 5 && (
        <p className="text-sm text-red-500 mb-4">Youâ€™ve reached the max token limit (5).</p>
      )}

      {message && <p className="text-sm text-center text-blue-600 mb-4">{message}</p>}

      <button
        onClick={handleDashboard}
        className="w-full mt-4 bg-green-600 text-white py-2 rounded hover:bg-green-700"
      >
        Go to Dashboard
      </button>
    </div>
  );
};

export default TokenCreation;
