// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Chart from '../components/Chart';
import moment from 'moment';

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [selectedToken, setSelectedToken] = useState(null);
  const [showChart, setShowChart] = useState(true);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [countdown, setCountdown] = useState('');

  const token = localStorage.getItem('token');

  const fetchUser = async () => {
    try {
      const res = await axios.get('https://profitpilot-backend.onrender.com/api/user', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(res.data);
      if (res.data.tokens && res.data.tokens.length > 0) {
        setSelectedToken(res.data.tokens[0]._id);
      }
    } catch (err) {
      console.error(err);
      localStorage.removeItem('token');
      navigate('/login');
    } finally {
      setLoading(false);
    }
  };

  const handleMining = async () => {
    setError('');
    setLoading(true);
    try {
      await axios.post('https://profitpilot-backend.onrender.com/api/mine', {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchUser();
    } catch (err) {
      setError('Mining failed. Try again.');
    } finally {
      setLoading(false);
    }
  };

  const calculateCountdown = () => {
    const now = moment();
    const target = moment('2025-06-06T00:00:00');
    const diff = moment.duration(target.diff(now));
    const format = `${diff.months()}m ${diff.days()}d ${diff.hours()}h ${diff.minutes()}m`;
    setCountdown(format);
  };

  useEffect(() => {
    fetchUser();
    calculateCountdown();
    const timer = setInterval(calculateCountdown, 60000); // Update every minute
    return () => clearInterval(timer);
  }, []);

  if (loading || !user) {
    return <div className="text-white text-center mt-20 text-xl animate-pulse">Loading Dashboard...</div>;
  }

  return (
    <div className="text-white">
      <h2 className="text-3xl font-bold text-orange-500 mb-6">Welcome, {user.username || 'User'}</h2>

      {/* Stats Card */}
      <div className="bg-[#1a1a1a] p-6 rounded-2xl mb-6 shadow-md space-y-3">
        <h3 className="text-xl font-semibold text-orange-400">Your Stats</h3>
        <div className="grid grid-cols-2 sm:grid-cols-3 gap-4 text-sm sm:text-base">
          <p><strong>Balance:</strong> ${user.balance.toFixed(2)}</p>
          <p><strong>Boost Rate:</strong> x{user.boostRate}</p>
          <p><strong>Tokens:</strong> {user.tokens.length}</p>
          <p><strong>Referrals:</strong> {user.referrals.length}</p>
          <p><strong>Next Reset:</strong> {countdown}</p>
        </div>

        <button
          onClick={handleMining}
          disabled={loading}
          className="mt-4 w-full bg-orange-600 hover:bg-orange-700 text-black font-bold py-2 px-4 rounded"
        >
          {loading ? 'Mining...' : 'Start Mining'}
        </button>

        {error && <p className="text-red-500 text-sm mt-2">{error}</p>}
      </div>

      {/* Chart */}
      <div className="bg-[#1a1a1a] p-6 rounded-2xl shadow-md">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-xl font-semibold text-orange-400">Performance Chart</h3>
          {user.tokens.length > 1 && (
            <select
              value={selectedToken}
              onChange={(e) => setSelectedToken(e.target.value)}
              className="bg-black text-white border border-orange-500 rounded px-2 py-1"
            >
              {user.tokens.map((token) => (
                <option key={token._id} value={token._id}>{token.name}</option>
              ))}
            </select>
          )}
        </div>

        {showChart && selectedToken && (
          <Chart tokenId={selectedToken} />
        )}
      </div>

      {/* Boost Button */}
      <div className="mt-6 text-center">
        <button
          onClick={() => navigate('/boost')}
          className="bg-orange-500 hover:bg-orange-600 text-black font-semibold px-6 py-2 rounded-xl shadow"
        >
          Boost Income
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
