import React, { useEffect, useState } from "react";
import axios from "axios";

const API = "https://profitpilot-backend.onrender.com";

const Admin = () => {
  const token = localStorage.getItem("token");
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState("");
  const [announcement, setAnnouncement] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get(`${API}/api/admin/users`, {
          headers: { "x-auth-token": token },
        });
        setUsers(res.data.users || []);
      } catch (err) {
        console.error("Failed to fetch users");
      }
    };
    fetchUsers();
  }, []);

  const handleReward = async (userId) => {
    try {
      await axios.post(
        `${API}/api/admin/reward`,
        { userId },
        { headers: { "x-auth-token": token } }
      );
      setSuccess("Reward sent!");
    } catch {
      setMessage("Failed to send reward.");
    }
  };

  const handleAnnouncement = async () => {
    try {
      await axios.post(
        `${API}/api/admin/announcement`,
        { text: announcement },
        { headers: { "x-auth-token": token } }
      );
      setSuccess("Announcement sent!");
    } catch {
      setMessage("Failed to send announcement.");
    }
  };

  const userEmail = JSON.parse(atob(token.split(".")[1])).email;
  if (userEmail !== "larryryh76@gmail.com") return <p>Access denied.</p>;

  return (
    <div className="admin-panel">
      <h2>Admin Panel</h2>

      {message && <p style={{ color: "red" }}>{message}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}

      <h3>Send Announcement</h3>
      <textarea
        value={announcement}
        onChange={(e) => setAnnouncement(e.target.value)}
        placeholder="Enter announcement message"
      />
      <button onClick={handleAnnouncement}>Send</button>

      <h3>All Users</h3>
      <ul>
        {users.map((user) => (
          <li key={user._id}>
            {user.email} — ${user.income.toFixed(2)} — Boost x{user.boostRate}
            <button onClick={() => handleReward(user._id)}>Send Reward</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Admin;
