import React, { useEffect, useState } from "react";
import axios from "axios";

const Referral = () => {
  const [referrals, setReferrals] = useState(0);
  const [link, setLink] = useState("");
  const [copied, setCopied] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");

    axios
      .get("https://profitpilot-backend.onrender.com/api/users/profile", {
        headers: { "x-auth-token": token },
      })
      .then((res) => {
        setReferrals(res.data.referrals || 0);
        const userId = res.data._id || res.data.id;
        if (userId) {
          setLink(`https://profit-pilot-frontend.vercel.app/register?ref=${userId}`);
        }
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

  const copyToClipboard = () => {
    navigator.clipboard.writeText(link);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="p-4 text-white">
      <h2 className="text-2xl font-bold mb-4">Referral Program</h2>
      <p className="mb-2">Youâ€™ve referred: <strong>{referrals}</strong> users</p>
      <p className="mb-4">Earn <strong>$2</strong> for every user that signs up using your link!</p>

      <div className="bg-gray-900 p-4 rounded-lg shadow-md border border-gray-700">
        <p className="text-sm mb-2 text-green-400 break-all">{link || "Loading..."}</p>
        <button
          onClick={copyToClipboard}
          className="bg-blue-600 hover:bg-blue-700 px-3 py-1 rounded text-white text-sm"
        >
          {copied ? "Copied!" : "Copy Link"}
        </button>
      </div>
    </div>
  );
};

export default Referral;
